class ProductPage {

    get home() {
        return $('=Home')
    }
    get submit() {
        return $('.btn-success')
    }
    get name() {
        return $('//form/div[1]/input')
    }
    get email() {
        return $("input[name='email']")
    }
    get password() {
        return $("#exampleInputPassword1")
    }
    get checkbox() {
        return $("#exampleCheck1")
    }
    get dropdown() {
        return $("#exampleFormControlSelect1")
    }
    get employment() {
        return $$(".form-group div")
    }
    get birthdate() {
        return $("input[name='bday']")
    }
    get shop() {
        return $('=Shop')
    }
    get category() {
        return $('=Category 1')
    }



    async expects() {
        await expect($(".row div h1")).toHaveTextContaining('Shop Name')
    }

